    private void btnborrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnborrarActionPerformed
        if (JOptionPane.showConfirmDialog(null, "¡ADVERTENCIA! ¿ESTA SEGURO?", "SALIR", JOptionPane.YES_NO_CANCEL_OPTION)==0) {
            try {
                PreparedStatement ps = cn.prepareStatement("DELETE FROM productos WHERE id='"+txtid.getText()+"'");
                int indice=ps.executeUpdate();
                        if(indice>0){
                            mostrardatos();
                        } else {
                        JOptionPane.showMessageDialog(rootPane, "no selecciono nada.");
                        }
            } catch (Exception e) {
                JOptionPane.showMessageDialog(rootPane, "ERROR.");
            }
        } else {
        }
    }//GEN-LAST:event_btnborrarActionPerformed

    private void btnanadirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnanadirActionPerformed
        try {
            PreparedStatement ps = cn.prepareStatement("INSERT INTO productos (nombreProducto,marca,stock,proveedor) VALUES (?,?,?,?)");
            ps.setString(1, txtnombre.getText());
            ps.setString(2, txtmarca.getText());
            ps.setString(3, txtstock.getText());
            ps.setString(4, txtproveedor.getText());
            ps.executeUpdate();
            JOptionPane.showMessageDialog(null, "SE GUARDO TODO DE MANERA CORRECTA.");
            mostrardatos();
            limpiartxt();
        } catch (Exception e) {
            System.out.println("ERROR");
        } 
    }//GEN-LAST:event_btnanadirActionPerformed

    private void jtabledatosMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jtabledatosMouseClicked
        int fila=this.jtabledatos.getSelectedRow();
        this.txtid.setText(this.jtabledatos.getValueAt(fila, 0).toString());
        this.txtnombre.setText(this.jtabledatos.getValueAt(fila, 1).toString());
        this.txtmarca.setText(this.jtabledatos.getValueAt(fila, 2).toString());
        this.txtstock.setText(this.jtabledatos.getValueAt(fila, 3).toString());
        this.txtproveedor.setText(this.jtabledatos.getValueAt(fila, 4).toString());
        mostrardatos();
    }//GEN-LAST:event_jtabledatosMouseClicked

    private void btnactualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnactualizarActionPerformed
        try {
            PreparedStatement ps=cn.prepareStatement("UPDATE productos set nombreProducto='"+txtnombre.getText()+"',marca='"+txtmarca.getText()+"',stock='"+txtstock.getText()+"',proveedor='"+txtproveedor.getText()+"' WHERE id='"+txtid.getText()+"'");
            int indice=ps.executeUpdate();
            
            if (indice>0) {
                JOptionPane.showMessageDialog(rootPane, "SE ACTUALIZO DE MANERA CORRECTA.");
                mostrardatos();
            } else {
                JOptionPane.showMessageDialog(null, "NO SELECCIONO NADA PARA ACTUALIZAR.");
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "ERROR"+e);
        }
    
    }//GEN-LAST:event_btnactualizarActionPerformed

    
