    private void filtrar(){
    

    String[] registros=new String[5];
    DefaultTableModel modelo = new DefaultTableModel();
        

        modelo.addColumn("id");
        modelo.addColumn("Nombre producto");
        modelo.addColumn("Marca");
        modelo.addColumn("Stock");
        modelo.addColumn("Proveedor");
        jtabledatos.setModel(modelo);
        String consultasql="SELECT * FROM productos WHERE id like='"+"%"+txtfiltrar.getText()+"%"+"' or nombreProducto like='"+"%"+txtfiltrar.getText()+"%"+"' or marca like='"+"%"+txtfiltrar.getText()+"%"+"' or stock like='"+"%"+txtfiltrar.getText()+"%"+"' or proveedor like='"+"%"+txtfiltrar.getText()+"%'";
        

        
        String data[]=new String[5];
              
        Statement st;
        try{
        st=(Statement)cn.createStatement();
         ResultSet rs=st.executeQuery(consultasql); 
            while(rs.next()){ 
                data[0]=rs.getString(1);
                data[1]=rs.getString(2);
                data[2]=rs.getString(3);
                data[3]=rs.getString(4);
                data[4]=rs.getString(5);
                modelo.addRow(data);
            }
        }catch (SQLException e){
            System.out.println("Error al mostrar datos " + e);
        }
       
    }
     
